import {
  Image,
  Column,
  Row,
  Button,
  Container,
  Col,
  Accordion,
  Form,
  FormControl,
  FormLabel,
  Card,
  Badge,
} from "react-bootstrap";
import {
  useAccount,
  useContractRead,
  useContractWrite,
  usePrepareContractWrite,
  useWaitForTransaction,
} from "wagmi";
import { supabase } from "./supabaeClient";
import { ethers } from "ethers";
import contractInterface from "../contract-abi.json";
import contractInterface2 from "../wrapper-contract-abi.json";

import React, { useState, useEffect } from "react";
import Picker from "emoji-picker-react";
import { BsEmojiHeartEyesFill } from "react-icons/bs";
import { BsTwitter } from "react-icons/bs";
import { FaTelegramPlane } from "react-icons/fa";
import { FaDiscord } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";

function Home() {
  const wrapperAdd = "0x2Cc8342d7c8BFf5A213eb2cdE39DE9a59b3461A7";
  const [availableChecks, setAvailableChecks] = useState("");
  const [resetBool, setResetBool] = useState(1);
  const [userBool, setUserBool] = useState(99);
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({ userAddress: "", userCount: "" });
  const { userAddress, userCount } = user;

  const defaultTokenName = "Linagee";
  const [userAdd, setUserAdd] = useState("");
  const [inputStr, setInputStr] = useState("");
  const [tokenId, setTokenId] = useState("");
  const [wrappedOwner, setWrappedOwner] = useState(
    "0x2Cc8342d7c8BFf5A213eb2cdE39DE9a59b3461A7"
  );
  const [showPicker, setShowPicker] = useState(false);
  const [inputBytes, setBytes] = useState();
  const [owned, setOwnedBool] = useState(5);
  const [currentOwner, setOwner] = useState();
  const { isConnected } = useAccount();
  const account = useAccount({
    onConnect({ address, connector, isReconnected }) {
      console.log("Connected", { address, connector, isReconnected });
      setUserAdd(account.address);
      console.log(account.address);
      
    },
  });

  async function fetchUsers() {
    const { data } = await supabase.from("lnrRegistry").select();
    setUsers(data);
    console.log("data : ", data);
  }

  async function createUser() {
    await supabase
      .from("lnrRegistry")
      .insert([{ userAddress, userCount }])
      .single();
    setUser({ userAddress: "", userCount: "" });
    fetchUsers();
  }

  async function updateUser() {
    await supabase
      .from("lnrRegistry")
      .update([{ userCount: userCount }])
      .match({userAddress : userAddress});
    setUser({ userAddress: "", userCount: "" });
    fetchUsers();
  }

  useEffect(async () => {
    console.log(userAdd);
    fetchUsers();
    const index = users.findIndex(item => item.userAddress == userAdd);
    console.log(index);
    if(index >= 0) {
      setAvailableChecks(users[index].userCount);
      console.log(users[index].userCount);
      setUserBool(100);
      console.log(userBool);
    }
  }, [userAdd]);

  const LNRcontractConfig = {
    addressOrName: "0x5564886ca2C518d1964E5FCea4f423b41Db9F561",
    contractInterface: contractInterface,
  };

  const wrappercontractConfig = {
    addressOrName: "0x2Cc8342d7c8BFf5A213eb2cdE39DE9a59b3461A7",
    contractInterface: contractInterface2,
  };

  const truncate = (input, len) =>
    input.length > len ? `${input.substring(0, len)}...` : input;

  //----------------CHECK NAME-----------------

  const { refetch } = useContractRead({
    ...LNRcontractConfig,
    functionName: "owner",
    args: inputBytes,
  });

  const { refetch: wrapperFetch } = useContractRead({
    ...wrappercontractConfig,
    functionName: "nameToId",
    args: inputBytes,
  });

  const { refetch: wrapperFetchOwner } = useContractRead({
    ...wrappercontractConfig,
    functionName: "ownerOf",
    args: tokenId,
  });

  useEffect(async () => {
    const ownerRes = await wrapperFetchOwner(Number(tokenId));
    setWrappedOwner(ownerRes.data);
  }, [tokenId]);

  const handleClick = async () => {
    if (inputStr.length !== 0) {
      setResetBool(0);
      setOwnedBool(3);
      console.log("clicked");
      const res = await refetch();
      const tokenRes = (await wrapperFetch()).data.toString();

      setTokenId(tokenRes);
      console.log(tokenId);
      console.log(`owner is:`, res.data);

      if (
        typeof res.data !== "undefined" &&
        res.data.toString() !== "0x0000000000000000000000000000000000000000"
      ) {
        setOwner(res.data.toString());
        setOwnedBool(1);
        console.log("Not available");
      }
      if (
        typeof res.data !== "undefined" &&
        res.data.toString() == "0x0000000000000000000000000000000000000000"
      ) {
        setOwnedBool(0);
        console.log("Available!");
      }
    }
  };

  //----------STRING TO BYTES32------------------

  useEffect(() => {
    setResetBool(1);
    console.log("input : ", inputStr);
    if (typeof inputStr !== "undefined" && inputStr !== "") {
      const bytes = setBytes(
        ethers.utils.formatBytes32String(inputStr).toString()
      );
      console.log(bytes);
    }
  }, [inputStr]);

  //-------------OpenSea Match---------------------
  const { config: reserveConfig } = usePrepareContractWrite({
    ...LNRcontractConfig,
    functionName: "reserve",
    args: inputBytes,
  });

  const {
    data: reserveData,
    write: reserve,
    isLoading: reserveLoading,
    isSuccess: reserveStarted,
    error: reserveError,
  } = useContractWrite(reserveConfig);

  //----------TRACK TRANSACTION------------------

  const {
    data: txData,
    isSuccess: txSuccess,
    error: txError,
  } = useWaitForTransaction({
    hash: reserveData?.hash,
  });

  const isMinted = txSuccess;

  const onEmojiClick = (event, emojiObject) => {
    setInputStr((prevInput) => prevInput + emojiObject.emoji);
    setShowPicker(false);
    console.log(inputStr);
  };
  return (
    <div id="container1">
      <Row style={{ justifyContent: "center", marginTop: "20px" }}>
        <Row>
          {userAdd}
          <input
            placeholder="userAddress"
            value={userAddress}
            onChange={(e) => setUser({ ...user, userAddress: e.target.value })}
          ></input>
          <input
            placeholder="userCount"
            value={userCount}
            onChange={(e) => setUser({ ...user, userCount: e.target.value })}
          ></input>
          <Button onClick={createUser}>Create User</Button>
          <Button onClick={updateUser}>Update User</Button>
        </Row>
        {owned == 5 && (
          <Card className="fluid" style={{ width: "20rem" }}>
            <Button
              variant="primary"
              size="md"
              onClick={handleClick}
              style={{ height: "250px", marginTop: "0.7em", fontSize: "1.5em" }}
            >
              {inputStr.length == 0 ? defaultTokenName : inputStr}
            </Button>

            <Card.Body>
              <Card.Title>
                <p style={{ marginTop: 5, color: "black" }}>Status : </p>
              </Card.Title>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>OpenSea Match : </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>Token ID : </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>Owner : </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>ByteCode : </p>
                <span>{inputBytes}</span>
              </Card.Text>
              <Row>
                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="primary"
                  size="md"
                  onClick={handleClick}
                >
                  Search
                </Button>

                <Row className="p-0 mt-2 justify-content-center align-content-around">
                  <Col className="justify-content-center align-items-center">
                    <FormControl
                      className="m-0"
                      type="string"
                      value={inputStr}
                      placeholder="Type Here"
                      required
                      onChange={(e) => setInputStr(e.target.value)}
                    ></FormControl>
                  </Col>
                  <Col
                    id="cent"
                    className="justify-content-start pt-1 align-items-center col-1 col-sm-1 col-md-1 col-lg-1"
                    style={{ backgroundColor: "ffffff" }}
                  >
                    <BsEmojiHeartEyesFill
                      id="grow"
                      style={{ fontSize: "1.5em" }}
                      onClick={() => setShowPicker((val) => !val)}
                    />
                  </Col>
                  {showPicker && (
                    <Picker
                      pickerStyle={{ width: "100%", alignItems: "center" }}
                      onEmojiClick={onEmojiClick}
                    />
                  )}
                </Row>

                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="warning"
                  size="md"
                  onClick={() => reserve?.()}
                  disabled
                >
                  {reserveLoading && "Pending..."}
                  {reserveStarted && "Minting..."}
                  {!reserveLoading && !reserveStarted && "Register Only"}
                </Button>
                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="warning"
                  size="md"
                  onClick={handleClick}
                  disabled
                >
                  Mint To OpenSea Only
                </Button>
                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="warning"
                  size="md"
                  onClick={handleClick}
                  disabled
                >
                  Register &#38; Mint To Opensea
                </Button>
              </Row>
            </Card.Body>
          </Card>
        )}
        {owned == 3 && (
          <Card style={{ width: "18rem" }}>
            <Button
              variant="primary"
              size="md"
              onClick={handleClick}
              style={{ height: "250px", marginTop: "0.7em", fontSize: "1.5em" }}
            >
              {inputStr.length == 0 ? defaultTokenName : inputStr}
            </Button>
            <Card.Body>
              <Card.Title>
                <p style={{ marginTop: 5, color: "black" }}>
                  Status :{" "}
                  <span style={{ color: "rgb(255, 190, 46)" }}>Fetching</span>{" "}
                </p>
              </Card.Title>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>
                  OpenSea Match :{" "}
                  <span style={{ color: "rgb(255, 190, 46)" }}>Fetching</span>
                </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>
                  Token ID :{" "}
                  <span style={{ color: "rgb(255, 190, 46)" }}>Fetching</span>
                </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>
                  Owner :{" "}
                  <span style={{ color: "rgb(255, 190, 46)" }}>Fetching</span>
                </p>
              </Card.Text>
              <Card.Text>
                <p style={{ marginTop: 5, color: "black" }}>ByteCode : </p>
                <span>{inputBytes}</span>
              </Card.Text>
              <Row>
                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="primary"
                  size="md"
                  onClick={handleClick}
                >
                  Search
                </Button>
                <Row className="p-0 mt-2 justify-content-center align-content-around">
                  <Col className="justify-content-center align-items-center">
                    <FormControl
                      className="m-0"
                      type="string"
                      value={inputStr}
                      placeholder="Type Here"
                      required
                      onChange={(e) => setInputStr(e.target.value)}
                    ></FormControl>
                  </Col>
                  <Col
                    id="cent"
                    className="justify-content-start pt-1 align-items-center col-1 col-sm-1 col-md-1 col-lg-1"
                    style={{ backgroundColor: "ffffff" }}
                  >
                    <BsEmojiHeartEyesFill
                      id="grow"
                      style={{ fontSize: "1.5em" }}
                      onClick={() => setShowPicker((val) => !val)}
                    />
                  </Col>
                  {showPicker && (
                    <Picker
                      pickerStyle={{ width: "100%", alignItems: "center" }}
                      onEmojiClick={onEmojiClick}
                    />
                  )}
                </Row>
                <Button
                  style={{ marginTop: "0.5em" }}
                  variant="warning"
                  size="md"
                  onClick={handleClick}
                  disabled
                >
                  Register Only
                </Button>
